import React, { useState, useEffect, useContext } from 'react'
import Konva from 'konva';
import useImage from 'use-image';
import { Stage, Layer, Star, Rect, Text, Image } from 'react-konva';
import request from "superagent";
import { UserCount } from '../App';
import Reset from './Reset';


const URLImage = (props) => {
  const [image] = useImage(props.url);
  console.log(image);
  return <Image
    image={image}
    scaleX={props.scaleX}
    scaleY={props.scaleY}
  />;
};

const Main = () => {
  const [count, setCount] = useContext(UserCount);
  const [sp_img_path, setSpImgPath] = useState([[0, 0], [0, 0]]);

  const getData = () => {
    request
      .get("/api/getSpectrumImg")
      .end((err, data) => {
        if (err) {
          console.error(err);
          return;
        }
          //setCount(data.body.data);
          setSpImgPath(data.body.img_path);
          console.log(sp_img_path);
          Image.displayName(image);
          return;
      });
  };

  const sendData = () => {
    request
    .get("/api/sendData")
    .query({data: -999})
    .end((err, data) => {
      if (err) {
        console.error(err);
        return;
      }
        console.log(data.body);
    });
  };

  return (
    <div className="Main">
      <div className="">
      <button onClick={() => getData()}>
          Get data
      </button>
      <button onClick={() => setCount(count - 1)}>
          Click me
      </button>
      </div>
      <div className="Stage1">
        <p> Stage1 </p>
        <Stage width={window.innerWidth} height={window.innerHeight/3}>
        <Layer>
          <URLImage
            url="https://konvajs.org/assets/lion.png"
            scaleX={10}
            scaleY={10}
          />
          <Text text="Try to drag a star" />
        </Layer>
      </Stage>
      </div>
      <div className="Stage2">
        <p> Stage2 </p>
        
      </div>
      
    </div>
  );
}

export default Main;
