import React, { useState, useEffect, useContext } from 'react'
import Konva from 'konva';
import useImage from 'use-image';
import { Stage, Layer, Star, Rect, Text, Image } from 'react-konva';
import request from "superagent";
import { UserCount } from '../App';
import Reset from './Reset';


const URLImage = (props) => {
  const [image] = useImage(props.url);
  console.log(image);
  return <Image
    image={image}
    scaleX={props.scaleX}
    scaleY={props.scaleY}
  />;
};

const Main = () => {
  const [count, setCount] = useContext(UserCount);
  const [specgram_data, setSpecgramData] = useState(null);
  const [data_ia_loaded, setDataIsLoaded] = useState(true);

  const getData = () => {
    request
      .get("/api/getSpectrumImg")
      .end((err, data) => {
        if (err) {
          console.error(err);
          return;
        }
          setSpecgramData(data.body);
          console.log(specgram_data)
          setDataIsLoaded(true);
          return;
      });
  };

  const sendData = () => {
    request
    .get("/api/sendData")
    .query({data: -999})
    .end((err, data) => {
      if (err) {
        console.error(err);
        return;
      }
        console.log(data.body);
    });
  };

  const stage_area = () => {
    if (data_ia_loaded) {
      return (
        <div className="Stage1">
          <p> Stage1 </p>
          <Stage width={window.innerWidth} height={window.innerHeight/3}>
            <Layer>
              <URLImage
                url="specgram.png"
                scaleX={1}
                scaleY={1}
              />
              <Text text="Try to drag a star" />
            </Layer>
          </Stage>
        </div>
      );
    } else {
      return <p> Please date load. </p>;
    }
  };

  return (
    <div className="Main">
      <div className="">
      <button onClick={() => getData()}>
          Get data
      </button>
      <button onClick={() => setCount(count - 1)}>
          Click me
      </button>
      </div>
      {stage_area()}
    </div>
  );
}

export default Main;
