import React, { useState, useEffect, useContext } from 'react'
import Konva from 'konva';
import useImage from 'use-image';
import { Stage, Layer, Star, Rect, Text, Image } from 'react-konva';
import request from "superagent";
import { UserCount } from '../App';
import Reset from './Reset';


const URLImage = (props) => {
  const [image] = useImage(props.url);
  console.log(useImage(props.url));
  return <Image
    image={image}
    scaleX={props.scaleX}
    scaleY={props.scaleY}
  />;
};

const LocalizedRect = (props) => {
  const [id, setId] = useState(props.id);
  return (
    <Rect
      x={props.x}
      y={props.y}
      width={props.width}
      height={props.height}
      fill={props.fill}
      stroke={props.stroke}
      strokeWidth={props.strokeWidth}
    />
  );
}

const Main = () => {
  const [count, setCount] = useContext(UserCount);
  const [img_data, setImgData] = useState(null);
  const [localized_data, setLocalizedData] = useState([]);
  const [data_ia_loaded, setDataIsLoaded] = useState(false);
  const [ldata_ia_loaded, setLDataIsLoaded] = useState(false);

  const getSpectrumImg = () => {
    request
      .get("/api/getSpectrumImg")
      .end((err, data) => {
        if (err) {
          console.error(err);
          return;
        }
          setImgData(data.body);
          console.log(img_data);
          setDataIsLoaded(true);
          return;
      });
  };

  const getLocalizedData = () => {
    request
      .get("/api/getLocalizedData")
      .end((err, data) => {
        if (err) {
          console.error(err);
          return;
        }
          console.log(data.body.localized_data.slice(1));
          setLocalizedData(data.body.localized_data);
          setLDataIsLoaded(true);
          return;
      });
  };

  const sendData = () => {
    request
    .get("/api/sendData")
    .query({data: -999})
    .end((err, data) => {
      if (err) {
        console.error(err);
        return;
      }
        console.log(data.body);
    });
  };

  const button_area = () => {
    if (data_ia_loaded) {
      return (
        <div className="button_area">
          <button onClick={() => getSpectrumImg()}>
            load Spectrum
          </button>
          <button onClick={() => getLocalizedData()}>
            load Localized data
          </button>
          <button onClick={() => setDataIsLoaded(false)}>
            resrt data
        </button>
      </div>
      );
    } else {
      return (
        <div className="button_area">
          <button onClick={() => getSpectrumImg()}>
            load Spectrum
          </button>
        </div>
      );
    }
  }

  const stage_area = () => {
    if (data_ia_loaded) {
      return (
        <div className="stage_area">
          <div className="Stage1">
            <p>Specgram</p>
            <Stage width={window.innerWidth} height={window.innerHeight/4}>
              <Layer>
                <URLImage
                  //url="https://raw.githubusercontent.com/yamatakeru/image_src/master/specgram.png"
                  url={img_data.specgram.img_path}
                  scaleX={1}
                  scaleY={0.5}
                />
                <Text text="specgram" />
              </Layer>
            </Stage>
          </div>
          <div className="Stage2">
          <p>Music</p>
          <Stage width={window.innerWidth} height={window.innerHeight/4}>
            <Layer>
              <URLImage
                //url="https://raw.githubusercontent.com/yamatakeru/image_src/master/music.png"
                url={img_data.music.img_path}
                scaleX={1}
                scaleY={0.5}
              />
              <Text text="music" />
              {localized_data.slice(1).map(ld => {
                return (
                  <LocalizedRect
                    id={ld[0]}
                    x={(ld[2] / img_data.music.t_max) * 992}
                    y={((-ld[4] + 180) / 360) * (740 / 2) - 10}
                    width={(ld[3] / img_data.music.t_max) * 992}
                    height={20}
                    fill="red"
                    stroke='black'
                    strokeWidth={2}
                  />
                )})
              }
            </Layer>
          </Stage>
        </div>
      </div>
      );
    } else {
      return <p> Please date load. </p>;
    }
  };

  return (
    <div className="Main">
      {button_area()}
      {stage_area()}
    </div>
  );
}

export default Main;
