import React, { useState, useEffect, useContext } from 'react'
import Konva from 'konva';
import useImage from 'use-image';
import { Stage, Layer, Star, Rect, Text, Image } from 'react-konva';
import request from "superagent";
import { UserCount } from '../App';
import Reset from './Reset';


const URLImage = (props) => {
  const [image] = useImage(props.url);
  return <Image
    image={image}
    scaleX={props.scaleX}
    scaleY={props.scaleY}
  />;
};

const Main = () => {
  const [count, setCount] = useContext(UserCount);

  const sendData = () => {
    request
    .get("/api/sendData")
    .query({data: -999})
    .end((err, data) => {
      if (err) {
        console.error(err);
        return;
      }
        console.log(data.body);
    });
  };

  const handleDragStart = e => {
    e.target.setAttrs({
      shadowOffset: {
        x: 15,
        y: 15
      },
      scaleX: 1.1,
      scaleY: 1.1
    });
  };

  const handleDragEnd = e => {
    e.target.to({
      duration: 0.5,
      easing: Konva.Easings.ElasticEaseOut,
      scaleX: 1,
      scaleY: 1,
      shadowOffsetX: 5,
      shadowOffsetY: 5
    });
  };

  return (
    <div className="Main">
      <div className="">
      <button onClick={() => setCount(count + 1)}>
          Click me
      </button>
      <button onClick={() => setCount(count - 1)}>
          Click me
      </button>
      </div>
      <div className="Stage1">
        <p> Stage1 </p>
        <Stage width={window.innerWidth} height={window.innerHeight/3}>
        <Layer>
          <URLImage
            url="https://konvajs.org/assets/lion.png"
            scaleX={10}
            scaleY={10}
          />
          <Text text="Try to drag a star" />
          {[...Array(10)].map((_, i) => (
            <Rect
              key={i}
              x={10}
              y={10}
              width={100}
              height={50}
              fill='green'
              stroke='black'
              strokeWidth={4}
              onDragStart={e => handleDragStart(e)}
              onDragEnd={e => handleDragEnd(e)}
            />
          ))}
        </Layer>
      </Stage>
      </div>
      <div className="Stage2">
        <p> Stage2 </p>
        
      </div>
      
    </div>
  );
}

export default Main;
